import React, { useEffect, useState } from "react";
import { api } from "../api/api"; // 경로 수정

export default function BoardInput({
  SubmitBoard,
  boardData,
  ChangeValue,
  handleFileChange,
}) {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    api.get("/categories")
      .then((response) => {
        setCategories(response.data);
      })
      .catch((error) => {
        console.error("Error fetching categories:", error);
      });
  }, []);

  return (
    <form onSubmit={SubmitBoard} encType="multipart/form-data">
      <label>
        제목 :
        <input
          type="text"
          placeholder="Enter Title"
          name="title"
          value={boardData.title}
          onChange={ChangeValue}
        />
      </label>
      <br />
      <label>
        내용 :
        <textarea
          placeholder="Enter Content"
          name="content"
          value={boardData.content}
          onChange={ChangeValue}
        />
      </label>
      <br />
      <label>
        카테고리 :
        <select name="categoryId" value={boardData.categoryId} onChange={ChangeValue}>
          <option value="">Select a category</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
      </label>
      <br />
      <input type="file" name="files" onChange={handleFileChange} multiple />
      <br />
      <button type="submit">완료</button>
    </form>
  );
}
