import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import BoardInput from "../../components/BoardInput";
import { api } from "../../api/api";

export default function Write(props) {
  const [board, setBoard] = useState({
    title: "",
    content: "",
    files: [],
    category: "",
  });

  const [categories, setCategories] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    api.get("/categories")
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error("Error fetching categories:", error);
      });
  }, []);

  function ChangeValue(event) {
    const { name, value } = event.target;
    setBoard(prevBoard => ({
      ...prevBoard,
      [name]: name === "category" ? parseInt(value) : value, // category를 정수형으로 변환
    }));
  }

  function handleFileChange(event) {
    setBoard(prevBoard => ({
      ...prevBoard,
      files: Array.from(event.target.files),
    }));
  }

  function SubmitBoard(event) {
    event.preventDefault();

    const formData = new FormData();
    formData.append("title", board.title);
    formData.append("content", board.content);
    formData.append("category", board.category); // 카테고리를 정수형으로 추가

    board.files.forEach((file) => formData.append("files", file));

    api.post(`/board`, formData)
      .then((response) => {
        alert("게시글 작성에 성공하였습니다.");
        props.fetchBoards();
        navigate("/board");
      })
      .catch((error) => {
        alert("게시글 작성에 실패하였습니다.");
        console.error("Error:", error);
      });
  }

  return (
    <div>
      <select name="category" value={board.category} onChange={ChangeValue}>
        <option value="">Select Category</option>
        {categories.map(category => (
          <option key={category.id} value={category.id}>{category.name}</option>
        ))}
      </select>

      <BoardInput
        SubmitBoard={SubmitBoard}
        boardData={board}
        ChangeValue={ChangeValue}
        handleFileChange={handleFileChange}
        newBoard={props.newBoard}
      />
    </div>
  );
}
