import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { closeModal } from '../redux/modalAction';

const MessageModal = ({children}) => {
  const isOpen = useSelector(state => state.modal.isOpen);
  const dispatch = useDispatch();

  if (!isOpen) return null;

  return (
    <div style={{position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', backgroundColor: 'white', padding: '20px', zIndex: 1000}}>
      {children}
      <button onClick={() => dispatch(closeModal())}>닫기</button>
    </div>
  );
};

export default MessageModal;
