import { useEffect, useState } from "react";
import { api } from "../../api/api";

const MessageDetail = ({id, setDetail}) => {

    const [messageDetail, setMessageDetail] = useState(null);

    useEffect(() => {
        api.get(`message/${id}`)
        .then((res) => { setMessageDetail(res.data)})
        .catch((error) => {console.error(error)})
    })

    //if(messageDetail === null) return ( <div>Loading...</div>);
    return (
        <div>
            <h5>제목:{messageDetail.title}</h5>
            <span>{messageDetail.sendMember}</span>&nbsp;
            <span>{messageDetail.receiveMember}</span>&nbsp;
            <span>{messageDetail.sendTime}</span>
            <br/>
            <p>내용:{messageDetail.text}</p>
            <button onClick={() => setDetail(0)}>목록</button>
        </div>
    );
}

export default MessageDetail;
