import React, { useEffect, useState } from "react";
import { useDispatch } from 'react-redux'; // Redux를 사용하는 경우
import { openModal } from '../../redux/modalAction'; // 모달을 열기 위한 액션
import { api } from "../../api/api";
import MessageModal from "../../components/MessageModal";

const Message = () => {
    const [receiveMessages, setReceiveMessages] = useState([]);

    const dispatch = useDispatch(); // Redux를 사용하는 경우

    useEffect(() => {
        api.get(`/message/received`)
        .then((res) => { setReceiveMessages(res.data);})
        .catch((error) => { console.error(error);});

        // 모달을 열기 위한 액션 디스패치
        dispatch(openModal()); // Redux를 사용하는 경우
    }, [dispatch])

    return(
        <MessageModal>
          <div>
              <h3>쪽지함</h3>
              <div> 보낸사람 | 제목 | 날짜</div>
              {receiveMessages.length > 0 ? (
                receiveMessages.map(message => (
                    <div key={message.id}>
                      {message.sendMember} | {message.title} | {message.sendTime}
                    </div>
                  ))
              ) : (
             <div>받은 쪽지가 없습니다.</div>
             )} 
          </div>
        </MessageModal>
    );
}

export default Message;
