import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import BoardInput from "../../components/BoardInput";
import { api } from "../../api/api";
import BoardList from "./BoardList";

export default function Write(props) {
  let token = localStorage.getItem("token");
  const [board, setBoard] = useState({
    title: "",
    content: "",
    files: [],
    category: "", // 카테고리 추가
  });

  const [categories, setCategories] = useState([]); // 카테고리 상태 추가

  const navigate = useNavigate();

  useEffect(() => {
    // 카테고리 데이터를 가져오는 API 호출
    api.get("/categories")
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error("Error fetching categories:", error);
      });
  }, []);

  function ChangeValue(event) {
    setBoard({
      ...board,
      [event.target.name]: event.target.value,
    });
    console.log(board);
  }

  function handleFileChange(event) {
    setBoard({
      ...board,
      files: Array.from(event.target.files),
    });
    console.log(board);
  }

  function SubmitBoard(event) {
    event.preventDefault();

    const formData = new FormData();
    formData.append("title", board.title);
    formData.append("content", board.content);
    formData.append("category", board.category); // 카테고리 추가

    board.files.forEach((file) => formData.append("files", file));
    api.post(`board`, formData)
      .then((data) => {
        console.log(data);
        alert("게시글 작성에 성공하였습니다.");
        props.fetchBoards();
        navigate("/board");
      })
      .catch((error) => {
        alert("게시글 작성에 실패하였습니다.");
        console.error("Error:", error);
      });
  }

  //input 분리
  return (
    <div>
      <select name="category" value={board.category} onChange={ChangeValue}>
        <option value="">Select Category</option>
        {categories.map(category => (
          <option key={category.id} value={category.id}>{category.name}</option>
        ))}
      </select>

      <BoardInput
        SubmitBoard={SubmitBoard}
        boardData={board}
        ChangeValue={ChangeValue}
        handleFileChange={handleFileChange}
        newBoard={props.newBoard}
      />
    </div>
  );
}
