  //       if (res.status === 201) {
  //         return res.json();
  //       } else {
  //         return null;
  //       }
  //     })
  //     .then((res) => {
  //       if (res !== null) {
  //         alert("게시글 작성이 완료 되었습니다.");
  //         navigate("/");
  //       } else {
  //         alert("게시글 등록 실패.");
  //       }
  //     });
  // }

  //input 분리
  return (
    <BoardInput
      SubmitBoard={SubmitBoard}
      boardData={board}
      ChangeValue={ChangeValue}
    />
  );
}
