import React, { useEffect, useState } from "react";
import { Route, Routes } from "react-router-dom";
import BoardItem from "./BoardItem";
import BoardHeader from "./BoardHeader";
import Write from "./Write";
import BoardDetail from "./BoardDetail";
import Update from "./Update";
import { api } from "../../api/api";
import "../../style/page.css";
import AreaFiltering from "../../components/AreaFiltering";

export default function BoardList() {
  const [boards, setBoards] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedAreas, setSelectedAreas] = useState([]);

  useEffect(() => {
    fetchBoards(currentPage);
  }, [currentPage, selectedAreas]);

  const fetchBoards = (page, term) => {
    const addressesQuery = selectedAreas.length > 0 ? `&addresses=${selectedAreas.join(',')}` : "";
    api
      .get(`board/filter?page=${page}&size=10&keyword=${term || ""}${addressesQuery}`)
      .then((response) => {
        setBoards(response.data.content);
        setCurrentPage(response.data.number);
        setTotalPages(response.data.totalPages);
      });
  };

  const previousPage = () => {
    if (currentPage > 0) {
      setCurrentPage(currentPage - 1);
    }
  };

  const nextPage = () => {
    if (currentPage < totalPages - 1) {
      setCurrentPage(currentPage + 1);
    }
  };

  const pageNumbers = [];
  for (let i = 0; i < totalPages; i++) {
    pageNumbers.push(
      <button key={i} onClick={() => setCurrentPage(i)}>
        {i + 1}
      </button>
    );
  }

  const handleSearch = () => {
    fetchBoards(0, searchTerm);
  };

  const handleAreaChange = (areas) => {
    setSelectedAreas(areas);
  };

  const newBoard = (newBoard) => {
    setBoards([...boards, newBoard]);
  };

  return (
    <div>
      <AreaFiltering onAreaChange={handleAreaChange} />
      <BoardHeader />
      <Routes>
        <Route path="write" element={<Write fetchBoards={() => fetchBoards(currentPage)} />} />
        <Route path=":id" element={<BoardDetail fetchBoards={() => fetchBoards(currentPage)} />} />
        <Route path="update/:id" element={<Update />} />
      </Routes>
      {boards.map((board) => (
        <BoardItem key={board.id} board={board} fetchBoards={() => fetchBoards(currentPage)} />
      ))}
      <div className="left-padding">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
        <br />
        <button onClick={previousPage}>이전</button>
        {pageNumbers}
        <button onClick={nextPage}>다음</button>
      </div>
    </div>
  );
}
