import axios from "axios";
import React, { useState } from 'react';

const CommentList = (boardId,{comments}) => {
    const [visible,setVisible] = useState(10);

    const showMore = () => {
        setVisible((prevValue) => prevValue + 10);
    }

    const deleteComment = async() => {
        try {
            await axios.delete(`http://localhost:8080/board/${boardId}/comment/${comments.id}`)
        } catch {
            alert("삭제 실패")
        }
    }

    const updateComment = async() => {
        try {
            await axios.put(`http://localhost:8080/board/${boardId}/comment/${comments.id}`)
        } catch {
            alert("수정 실패")
        }
    }
    return (
        <div>
            <p>댓글&nbsp;{comments.length}</p>
            {comments.slice(0,visible).map((comment) => (
                <div key={comment.id}>
                    <span>{comment.memberNickName}</span>&nbsp;&nbsp;&nbsp;
                    <span>{comment.createTime}</span>&nbsp;
                    <button onClick={updateComment}>수정</button>&nbsp;
                    <button onClick={deleteComment}>삭제</button>
                    <p>{comment.content}</p>
                </div>
            ))}
            {visible < comments.length && (
                <p onClick={showMore}>더보기</p>
            )}
        </div>
    );
}

export default CommentList;
