import React, { useEffect, useState } from "react";
import BoardItem from "./BoardItem";
import BoardHeader from "./BoardHeader";
import Write from "./Write";
import BoardDetail from "./BoardDetail";
import Update from "./Update";
import { api } from "../../api/api";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import "../../style/page.css";
import AreaFiltering from "../../components/AreaFiltering";

export default function BoardList() {
  const [boards, setBoards] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedRegion, setSelectedRegion] = useState("");

  useEffect(() => {
    fetchBoards(currentPage);
  }, [currentPage, selectedRegion]);

  const fetchBoards = (page, term) => {
    let url = `board?page=${page}&size=10`;
    if (term) {
      url += `&keyword=${term}`;
    }
    if (selectedRegion) {
      url += `&region=${selectedRegion}`;
    }
    api.get(url).then((response) => {
      setBoards(response.data.content);
      setCurrentPage(response.data.number);
      setTotalPages(response.data.totalPages);
    });
  };

  const previousPage = () => {
    if (currentPage > 0) {
      setCurrentPage(currentPage - 1);
    }
  };

  const nextPage = () => {
    if (currentPage < totalPages - 1) {
      setCurrentPage(currentPage + 1);
    }
  };

  const pageNumbers = [];
  for (let i = 0; i < totalPages; i++) {
    pageNumbers.push(
      <button key={i} onClick={() => setCurrentPage(i)}>
        {i + 1}
      </button>
    );
  }

  const handleSearch = () => {
    fetchBoards(0, searchTerm);
  };

  const handleRegionChange = (region) => {
    setSelectedRegion(region);
    setCurrentPage(0); // 페이지를 초기화
  };

  return (
    <div>
      <AreaFiltering onAreaChange={handleRegionChange} />
      <BoardHeader />
      <Routes>
        <Route
          path="write"
          element={<Write fetchBoards={() => fetchBoards(currentPage)} selectedRegion={selectedRegion} />}
        />
        <Route
          path=":id"
          element={<BoardDetail fetchBoards={() => fetchBoards(currentPage)} />}
        />
        <Route path="update/:id" element={<Update />} />
      </Routes>
      {boards.map((board) => (
        <BoardItem
          key={board.id}
          board={board}
          fetchBoards={() => fetchBoards(currentPage)}
        />
      ))}
      <div className="left-padding">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
        <br />
        <button onClick={previousPage}>이전</button>
        {pageNumbers}
        <button onClick={nextPage}>다음</button>
      </div>
    </div>
  );
}
