import { useState, useEffect } from "react";
import axios from "axios";

function InputForm({ onSubmit, boardData, onChange, handleFileChange, categories }) {
  return (
    <form onSubmit={onSubmit} encType="multipart/form-data">
      <label>
        제목 :
        <input
          type="text"
          placeholder="Enter Title"
          name="title"
          value={boardData.title}
          onChange={onChange}
        />
      </label>
      <br />
      <label>
        내용 :
        <textarea
          placeholder="Enter Content"
          name="content"
          value={boardData.content}
          onChange={onChange}
        />
      </label>
      <br />
      <label>
        카테고리 :
        <select name="categoryId" value={boardData.categoryId} onChange={onChange}>
          <option value="">Select Category</option>
          {categories.map(category => (
            <option key={category.id} value={category.id}>{category.name}</option>
          ))}
        </select>
      </label>
      <br />
      <input type="file" name="files" onChange={handleFileChange} multiple />
      <br />
      <button type="submit">완료</button>
    </form>
  );
}

export default function BoardInput({ SubmitBoard, boardData, ChangeValue, handleFileChange }) {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const token = localStorage.getItem('token'); // 필요한 경우 토큰을 가져옴
        const response = await axios.get("http://localhost:8080/categories", {
          headers: {
            'Authorization': `Bearer ${token}` // 필요한 경우 인증 헤더 추가
          }
        });
        setCategories(response.data);
      } catch (error) {
        console.error("Error fetching categories:", error);
      }
    };
    fetchCategories();
  }, []);

  return (
    <InputForm
      onSubmit={SubmitBoard}
      boardData={boardData}
      onChange={ChangeValue}
      handleFileChange={handleFileChange}
      categories={categories}
    />
  );
}
