import React, { useEffect, useState, useCallback } from "react";
import { api } from "../api/api";
import { useDispatch, useSelector } from 'react-redux';
import { closeModal } from '../redux/modalAction';

const MessageModal = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const isOpen = useSelector(state => state.modal.isModalOpen);
  const dispatch = useDispatch();

  const [receiveMessages, setReceiveMessages] = useState([]);
  const [sendMessages, setSendMesssages] = useState([]);

  const [clickState,setClickState] = useState(true);

  const handleClickState = (isClick) => {
    setClickState(isClick);

    if(isClick && sendMessages.length === 0) {
      api.get(`/message/sent`)
      .then((res) => { setSendMesssages(res.data);})
      .catch((error) => { console.error(error);});
    }
  }
   // 드래그 시작 핸들러
   const startDragging = (e) => {
    setIsDragging(true);
    setStartPos({
      x: e.clientX - position.x,
      y: e.clientY - position.y,
    });
  };

  // 드래그 종료 핸들러
  const stopDragging = useCallback(() => {
    setIsDragging(false);
  }, []);

  useEffect(() => {
    api.get(`/message/received`)
      .then((res) => { setReceiveMessages(res.data);})
      .catch((error) => { console.error(error);});

    const onDragging = (e) => {
        if (isDragging) {
          setPosition({
            x: e.clientX - startPos.x,
            y: e.clientY - startPos.y,
          });
        }
      };
    
      if (isDragging) {
        window.addEventListener('mousemove', onDragging);
        window.addEventListener('mouseup', stopDragging);
      }
  
      return () => {
        window.removeEventListener('mousemove', onDragging);
        window.removeEventListener('mouseup', stopDragging);
      };
    }, [isDragging, stopDragging, startPos.x, startPos.y]);
  if (!isOpen) return null;

  return (
    <div 
      className="modalContainer" onMouseDown={startDragging}
      style={{ top: `calc(50% + ${position.y}px)`,
      left: `calc(50% + ${position.x}px)`, position: 'absolute' }}>
      <h3>쪽지함</h3>
      <div><span onClick={() => handleClickState(true)}>받은 쪽지</span> | <span onClick={() => handleClickState(false)}>보낸 쪽지</span></div>
      {clickState ? (
        <>
          <div>보낸 사람 | 제목 | 날짜</div>
            {receiveMessages.length > 0 ? (
              receiveMessages.map(message => (
                <div key={message.id}>
                  {message.sendMember} | {message.title} | {message.sendTime}
                </div>
              ))
            ) : (
          <div>받은 쪽지가 없습니다.</div>
          )}
        </>
      ) : (
      <>
        <div>받은 사람 | 제목 | 날짜</div>
          {sendMessages.length > 0 ? (
            sendMessages.map(message => (
              <div key={message.id}>
                {message.receiveMember} | {message.title} | {message.sendTime}
              </div>
            ))
          ) : (
        <div>보낸 쪽지가 없습니다.</div>
        )}
      </>
      )} 
      <button onClick={() => dispatch(closeModal())}>닫기</button>
    </div>
  );
};

export default MessageModal;
